void Set_Index(unsigned char index) {
  TFT_RS = 0;
  PMDIN = index;
  while(PMMODE & 0x8000); //Busy bit
}

void Write_Command(unsigned char command ) {
  TFT_RS = 1;
  PMDIN = command;
  while(PMMODE & 0x8000); //Busy bit
}

void Write_Data(unsigned int _data) {
  TFT_RS = 1;
  PMDIN = _data;
  while(PMMODE & 0x8000); //Busy bit
}


void Write_Command_SSD1963(unsigned char Command) {
  TFT_RS = 0;  //RS = 0 NOT DATA  D/C = 0
  PMDIN = Command;
  while(PMMODE & 0x8000); //Busy bit
}


void Write_Data_SSD1963(unsigned int _data) {
  TFT_RS = 1;   //RS = 1 = DATA   D/C = 1
  PMDIN = _data;
  while(PMMODE & 0x8000); //Busy bit
}

void Init_16Bit_PMP() {

  PMMODE = 0;
  PMCONBits.DUALBUF = 0;   //DUAL BUFFER FOR IN AND OUT
  PMMODEbits.MODE = 2;     // Master 2     slave =1
  PMMODEbits.WAITB = 0;    //Wait Time After Chip Select is Switched Before Strobe WAITB<1:0>: Data Setup to Read/Write Strobe Wait States bits(1) 11 = Data wait of 4 TPB; multiplexed address phase of 4 TPB  10 = Data wait of 3 TPB; multiplexed address phase of 3 TPB 01 = Data wait of 2 TPB; multiplexed address phase of 2 TPB 00 = Data wait of 1 TPB; multiplexed address phase of 1 TPB (default)
  PMMODEbits.WAITM = 1;    //Width Of Strobe Pulse 1111 = Wait of 16 TPB --0001 = Wait of 2 TPB-- 0000 = Wait of 1 TPB (default)
  PMMODEbits.WAITE = 1;    //Wait Time After Strobe Before Chip Select is De-selected bit 1-0WAITE<1:0>: Data Hold After Read/Write Strobe Wait States bits(1)

  PMMODEbits.MODE16 = 1;   // 1 = 16-bit mode: a read or write to the data register invokes a single 16-bit transfer   For read operations  11 = Wait of 3 TPB  00 = Wait of 0 TPB (default)
  PMCONbits.CSF = 0;       //chip select bit
  PMCONbits.PTRDEN = 1;    //READ STROBE
  PMCONbits.PTWREN = 1;    //WRITE STROBE
  PMCONbits.PMPEN = 1;

  TFT_Set_Active(Set_Index,Write_Command,Write_Data);// Required if using MikroE Library Anything Other Than My Write Number Function
}

