void Set_Time(){

    // RTCC registers unlock procedure
   SYSKEY = 0xAA996655;      // Write first unlock key to SYSKEY
   SYSKEY = 0x556699AA;      // Write second unlock key to SYSKEY   Section 5. Flash Programming   page 11

   RTCWREN_bit = 1;          // RTC Value registers can be written to by the user
   RTCOE_bit = 1;            // RTCC pin clock output enabled
   // RTSECSEL_bit = 1;      // RTCC Seconds Clock is selected for the RTCC pin
   ON_RTCCON_bit= 0;         // Turn off the RTCC module
   while(RTCCON&0x40);       // Wait for clock to be turned off
   RTCTIME = time;           // Set time
   RTCDATE = date;           // Set date
   ON_RTCCON_bit = 1;        // Turn on the RTCC module

   while(!(RTCCON&0x40));     // Wait for clock to be turned on  (RTCCLKON)

   SOSCEN_bit = 1;
   //  RTCCLKSEL<1:0>: RTCC Clock Select bits
   RTCCLKSEL1_bit = 0;
   RTCCLKSEL0_bit = 1;    //00 = RTCC uses the internal 32 kHz oscillator (INTOSC) //   RTCCLKSEL0_bit = 1;  //01 = RTCC uses the external 32.768 kHz Secondary Oscillator (SOSC)
}


