void Screen_Capture(unsigned int Start_Column, unsigned int End_Column, unsigned int Start_Row, unsigned int End_Row){

         unsigned long j;
         TFT_CS = 0;
         SRAM_CS = 1;

        TFT_Set_Index_Ptr(0x2A);                 //Set_column_address Set the column extent
        TFT_Write_Command_Ptr(Start_Column>>8);  //Start column number high byte
        TFT_Write_Command_Ptr(Start_Column);     //Start column number low byte
        TFT_Write_Command_Ptr(End_Column>>8);    //End column number high byte
        TFT_Write_Command_Ptr(End_Column);       //End column number low byte

        TFT_Set_Index_Ptr(0x2B);                 //Set Row Address
        TFT_Write_Command_Ptr(Start_Row>>8);     //Start page (row) number high byte
        TFT_Write_Command_Ptr(Start_Row);        //Start page (row) number low byte
        TFT_Write_Command_Ptr(End_Row>>8);       //End page (row) number high byte
        TFT_Write_Command_Ptr(End_Row);          //End page (row) number low byte

        Write_Command_SSD1963(0x2E);             //Read Memory Start

         for(j=1; j<130000; j++){

          SRAM_BUFFER[j] = PMDIN; //writing to a buffer

           while(PMMODE & 0x8000); //Busy bit

          }

          TFT_CS = 1;
}


